from collections import defaultdict

def minimum_new_roads_needed(n, m, s, roads):
    s += 1

    def read_graph():
        g = defaultdict(list)
        for (u, v) in roads:
            g[u - 1].append(v - 1)
        return g
    G = read_graph()
    vis = defaultdict(lambda : False)
    topo = []

    def dfs(u):
        for v in G[u]:
            if not vis[v]:
                vis[v] = True
                dfs(v)
        topo.append(u)
    for i in range(n):
        if not vis[i]:
            vis[i] = True
            dfs(i)
    vis.clear()
    vis[s] = True
    dfs(s)
    ans = 0
    for i in topo[::-1]:
        if not vis[i]:
            vis[i] = True
            ans += 1
            dfs(i)
    return ans

