def compute_single_digit_sum(test_cases):
    results = []
    for case in test_cases:
        res = 1
        for (length, digit) in case:
            res += length * digit
        s = str(res)
        while len(s) > 1:
            res = sum(map(int, list(s)))
            s = str(res)
        results.append(int(s))
    return results

