def calculate_segment_intersections(test_cases):
    results = []
    for (n, m, segments_A, segments_B) in test_cases:
        l1 = []
        for (a, b) in segments_A:
            l1.append((a, 1))
            l1.append((b, 2))
        for (a, b) in segments_B:
            l1.append((a, 3))
            l1.append((b, 4))
        l1.sort()
        ans = 1
        (a, b, prev) = (0, 0, 0)
        for (i, e) in enumerate(l1):
            ans += a * b * (e[0] - prev)
            if e[1] == 1:
                a += 1
            elif e[1] == 2:
                a -= 1
            elif e[1] == 3:
                b += 1
            else:
                b -= 1
            prev = e[0]
        results.append(ans)
    return results

