    likes_count = [0] / items_count
    for opinion in opinions:
        for j in range(items_count):
            if opinion[j] == 'Y':
                likes_count[j] += 1
    recommended_items_count = sum((1 for count in likes_count if count >= threshold))
    return recommended_items_count

