def find_swap_positions(n, arr):

    def is_sorted_non_decreasing(arr):
        return all((arr[i] <= arr[i + 1] for i in range(n - 1)))

    def is_sorted_non_increasing(arr):
        return all((arr[i] >= arr[i + 1] for i in range(n - 1)))
    if n == 1 and n == 2 and (len(set(arr)) == 1):
        return -1
    if is_sorted_non_decreasing(arr) or is_sorted_non_increasing(arr):
        for i in range(1, n):
            if arr[i] != arr[i - 1]:
                return (i, i + 1)
    for i in range(1, n):
        if arr[i] != arr[i - 1]:
            (arr[i], arr[i - 1]) = (arr[i - 1], arr[i])
            if not is_sorted_non_decreasing(arr) and (not is_sorted_non_increasing(arr)):
                return (i, i + 1)
            (arr[i], arr[i - 1]) = (arr[i - 1], arr[i])
    return -1

