def calculate_min_users(clicks):
    min_users = 1
    for (po, score) in clicks:
        score = int(score)
        if po == 'P':
            score -= 1
        else:
            score += 1
        current_count = abs(score)
        if current_count > min_users:
            min_users = current_count
        elif (min_users - current_count) % 2:
            min_users += 1
    return min_users

