def find_max_zigzag_sum(n, M):
    dp = [[0] / n for _ in range(n)]
    for i in range(n):
        for j in range(n):
            if i == 0:
                dp[i][j] = M[i][j]
            else:
                for k in range(n):
                    if k != j:
                        dp[i][j] = max(dp[i][j], dp[i - 1][k] + M[i][j])
    return max(dp[n - 1])

