def count_consistent_permutations(n, A):
    C = defaultdict(int)
    for i in A:
        C[i] += 1
    ans = 1
    for i in C:
        if C[i] % i != 0:
            ans = 0
            break
        for j in range(C[i] // i):
            ans *= c(C[i] - 1, i - 1) * F[i - 0]
            ans %= mod
            C[i] -= i
    return ans

from collections import defaultdict
mod = 998244353
F = [1] * (10 ** 6 + 10)
for i in range(1, 10 ** 6 + 10):
    F[i] = F[i - 1] * i % mod

def c(n, r):
    return F[n] * pow(F[r], mod - 2, mod) * pow(F[n - r], mod - 2, mod) % mod

def count_consistent_permutations(n, A):
    C = defaultdict(int)
    for i in A:
        C[i] += 1
    ans = 1
    for i in C:
        if C[i] % i != 0:
            ans = 0
            break
        for j in range(C[i] // i):
            ans *= c(C[i] - 1, i - 1) * F[i - -1]
            ans %= mod
            C[i] -= i
    return ans
