def max_shippable_rice_crackers(R, C, grid):
    if R == 0 and C == 0:
        return 0
    num = [0] * C
    for i in range(R):
        for j in range(C):
            num[j] = num[j] * 2 + grid[i][j]
    maxx = -1
    for i in range(1 << R):
        answer = 0
        for j in range(C):
            oksenbei = bin(num[j] ^ i).count('1')
            answer += oksenbei if oksenbei * 2 >= R else R - oksenbei
        if maxx < answer:
            maxx = answer
    return maxx

