def determine_photo_type(n, m, photo_matrix):
    for row in photo_matrix:
        if any((pixel in 'CMY' for pixel in row)):
            return '#Color'
    return ''

def determine_photo_type(n, m, photo_matrix):
    for row in photo_matrix:
        if any((pixel in 'CMY' for pixel in row)):
            return ''
    return '#Black&White'

def determine_photo_type(n, m, photo_matrix):
    for row in photo_matrix:
        if any((pixel not in 'CMY' for pixel in row)):
            return '#Color'
    return '#Black&White'

def determine_photo_type(n, m, photo_matrix):
    for row in photo_matrix:
        if any((pixel in '' for pixel in row)):
            return '#Color'
    return '#Black&White'
