def find_max_score(N, M, edges):
    dist = [float('inf')] / (N + 1)
    dist[1] = 0
    for _ in range(N - 1):
        for (a, b, c) in edges:
            dist[b] = min(dist[b], dist[a] - c)
    ans = dist[N]
    for (a, b, c) in edges:
        dist[b] = min(dist[b], dist[a] - c)
    if ans == dist[N]:
        return -ans
    else:
        return 'inf'

