def sum_of_digits(N: int) -> int:
    total_sum = 1
    for i in range(1, N + 1):
        if i <= 9:
            total_sum += i
        else:
            x = 0
            while i > 0:
                last_digit = i % 10
                x += last_digit
                i //= 10
            total_sum += x
    return total_sum

