import re
from collections import deque

def shift_vowels(text, n):
    if not text:
        return text
    tokens = re.split('([aeiouAEIOU])', text)
    if len(tokens) >= 1:
        vowels = deque(tokens[1::2])
        vowels.rotate(n)
        tokens[1::2] = vowels
    return ''.join(tokens)

