from math import factorial

def ncr2(n, r):
    return int(factorial(n) / (factorial(n - r) * factorial(r)))

def calculate_ways_to_climb_stairs(steps):
    ways = 1
    r = 0
    while steps - r >= r:
        ways += ncr2(steps - r, r)
        r += 1
    return ways % 2 ** 64

