        if n > 1:
            count += 1
        return count
    lcm = A * B / gcd(A, B)
    distinct_prime_factors = count_distinct_prime_factors(lcm)
    if distinct_prime_factors > 1 and count_distinct_prime_factors(distinct_prime_factors) == 1:
        return 'Yes'
    else:
        return 'No'

