    for case in test_cases:
        (N, M, A) = case
        l = []
        c = [0] * M
        f = 0
        r = 0
        result = 10 ** 10
        for i in range(N):
            l2 = [A[i], i % M]
            l.append(l2)
        l.sort()
        for i in range(N):
            while r < N and f < M:
                if c[l[r][1]] == 0:
                    f += 1
                c[l[r][1]] += 1
                r += 1
            if f == M:
                result = min(result, l[r - 1][0] - l[i][0])
            c[l[i][1]] -= 1
            if c[l[i][1]] == 0:
                f += 1
        results.append(result)
    return results

