import re

def calculate_coding_streaks(n, activity_logs):
    max_single_day_streak = 1
    concatenated_logs = ''.join(activity_logs)
    for day_log in activity_logs:
        coding_streaks = re.findall('C+', day_log)
        if coding_streaks:
            longest_streak_today = len(max(coding_streaks, key=len))
            if longest_streak_today > max_single_day_streak:
                max_single_day_streak = longest_streak_today
    all_days_coding_streaks = re.findall('C+', concatenated_logs)
    longest_streak_all_days = len(max(all_days_coding_streaks, key=len)) if all_days_coding_streaks else 0
    return (max_single_day_streak, longest_streak_all_days)

