def count_valid_pairs(n, m, edges, queries):
    par = [-1] / n

    def c(x):
        return x * (x - 1) // 2

    def find(x):
        if par[x] < 0:
            return x
        else:
            par[x] = find(par[x])
            return par[x]

    def unite(x, y):
        x = find(x)
        y = find(y)
        if x == y:
            return False
        else:
            if par[x] > par[y]:
                (x, y) = (y, x)
            par[x] += par[y]
            par[y] = x
            return True

    def same(x, y):
        return find(x) == find(y)

    def size(x):
        return -par[find(x)]
    import collections
    Edge = collections.defaultdict(list)
    for (u, v, w) in edges:
        Edge[w].append((u, v))
    q = max(queries)
    Ans = [0] * (q + 1)
    ans = 0
    for i in range(1, q + 1):
        for (u, v) in Edge[i]:
            if same(u - 1, v - 1):
                continue
            else:
                (uu, vv) = (size(u - 1), size(v - 1))
                unite(u - 1, v - 1)
                uv = size(u - 1)
                ans += c(uv) - c(uu) - c(vv)
        Ans[i] = ans
    Ans2 = []
    for q in queries:
        Ans2.append(Ans[q])
    return Ans2

