def max_rectangle_area(mat, r, c):
    hist = [[0 for _ in range(c)] for _ in range(r)]
    for j in range(c):
        hist[0][j] = mat[0][j]
    for i in range(1, r):
        for j in range(c):
            if mat[i][j]:
                hist[i][j] = hist[i - 1][j] + mat[i][j]
    for i in range(r):
        hist[i].sort(reverse=True)
    ans = 1
    for i in range(r):
        for j in range(c):
            ans = max(ans, hist[i][j] * (j + 1))
    return ans

