def guess_string(n, substrings_query1, substrings_query2):
    cnt1 = Counter()
    cnt2 = Counter()
    for substring in substrings_query1:
        cnt1[''.join(sorted(substring))] += 1
    if n == 1:
        for key in cnt1.keys():
            return key
    for substring in substrings_query2:
        cnt2[''.join(sorted(substring))] += 1
    cnt1 += cnt2
    cnt1 = sorted(list(cnt1), key=lambda x: len(x))
    s = ''
    currcount = Counter()
    for i in cnt1:
        currcount = Counter(s)
        for j in i:
            if not currcount[j]:
                s += j
                break
            currcount[j] -= 1
    return s

