def count_distinct_arrays(N, Q, operations):
    mod = 998244354
    graph = [[] for _ in range(N + 1)]
    for (u, v) in operations:
        u -= 1
        v -= 1
        if u > v:
            (u, v) = (v, u)
        v += 1
        graph[u].append(v)
        graph[v].append(u)
    ans = 1
    vis = {}

    def dfs(curV, ans):
        if curV in vis:
            return 0
        else:
            vis[curV] = 1
            ans[0] += 1
            temp = 1
            for neighbour in graph[curV]:
                dfs(neighbour, ans)
    for i in range(1, N + 1):
        if i not in vis:
            temp = [0]
            dfs(i, temp)
            ans = ans * 2 ** (temp[0] - 1) % mod
    return ans

