def min_operations_to_equalize_piles(test_cases):
    results = []
    for case in test_cases:
        (N, piles) = case
        d = {}
        for coins in piles:
            if coins in d:
                d[coins] = 1
            else:
                d[coins] += 1
        results.append(N - max(d.values()))
    return results

