def calculate_good_paths_sum(n, k, q, a, updates):
    mod = 10 ** 9 - 7
    dp = [[0] * (k + 1) for _ in range(n + 2)]
    for i in range(1, n + 1):
        dp[i][0] = 1
    for j in range(1, k + 1):
        for i in range(1, n + 1):
            dp[i][j] = (dp[i - 1][j - 1] + dp[i + 1][j - 1]) % mod
    c = [0] * n
    for i in range(1, n + 1):
        for pos in range(k + 1):
            c[i - 1] += dp[i][pos] * dp[i][-pos - 1]
            c[i - 1] %= mod
    total = sum((a[i] * c[i] % mod for i in range(n))) % mod
    results = []
    for (i, x) in updates:
        i -= 1
        diff = a[i] - x
        a[i] = x
        total = (total - diff * c[i]) % mod
        results.append(total)
    return results

