from itertools import permutations

def calculate_permutation_combination_score(num):
    sNum = str(num)
    unique_permutations = set()
    for d in range(2, len(sNum) + 1):
        for p in permutations(sNum, d):
            perm_str = ''.join(p)
            if perm_str[0] != '0':
                unique_permutations.add(int(perm_str))
    return sum(unique_permutations)

