import re

def count_occurrences(full_text: str, search_text: str, allow_overlap: bool=False) -> int:
    if not search_text:
        return 0
    pattern = f'(?=({search_text}))' if allow_overlap else search_text
    return len(re.findall(pattern, full_text))

