def determine_permutation_source(n, permutation):
    """"""
    kl = [0] * n
    k = 0
    for i in range(n):
        if kl[i] == 0:
            kl[i] = 1
            j = permutation[i] - 1
            k += 1
            while kl[j] == 0:
                kl[j] = 1
                j = permutation[j] - 1
    if k % 2 == 0:
        return 'Petr'
    else:
        return 'Um_nik'
