def diagonal_sort(matrix, n, m):
    diagonals = {}
    for i in range(n):
        for j in range(m):
            if i - j not in diagonals:
                diagonals[i - j] = [matrix[i][j]]
            else:
                diagonals[i - j].append(matrix[i][j])
    for (key, value) in diagonals.items():
        if key >= 0:
            value.sort(reverse=True)
        elif key < 0:
            value.sort()
        else:
            value[:] = value[::-1]
    for i in range(n):
        for j in range(m):
            matrix[i][j] = diagonals[i - j].pop()

