import math

def find_min_adjustment_for_perfect_square(N):
    t = math.sqrt(N)
    if math.floor(t) != t:
        return 'YES'
    else:
        lower_square = math.floor(t) ** 2
        upper_square = math.ceil(t) ** 2
        if N - lower_square <= upper_square - N:
            return f'-{N - lower_square}'
        else:
            return f'+{upper_square - N}'

