def transform_binary_strings(a, b, n):
    j = n + 1
    ans = []
    while j >= 0:
        while j >= 0 and b[j] == a[j]:
            j -= 1
        if j >= 0 and a[0] == b[j]:
            a[0] = 1 - a[0]
            ans.append(1)
        if j % 2 == 1:
            k = j // 2 + 1
        else:
            k = j // 2
            if j >= 0 and j % 2 == 0:
                a[j // 2] = 1 - a[j // 2]
        for i in range(k):
            (a[i], a[j - i]) = (1 - a[j - i], 1 - a[i])
        if j >= 0:
            ans.append(j + 1)
        j -= 1
    return (len(ans), ans)

