from collections import defaultdict

def solve_bender_problem(n, m, nail_coordinates, rod_lengths):
    tmp = nail_coordinates
    nails = [abs(a - c) - abs(b - d) for ((a, b), (c, d)) in zip(tmp, tmp[2:] + tmp[:2])]
    segments = defaultdict(list)
    for (i, s) in enumerate(rod_lengths, 1):
        segments[s].append(i)
    for shift in (-1, 0):
        res = [-1] * n
        for nailidx in range(shift, n + shift, 2):
            nail = nails[nailidx]
            if nail in segments and segments[nail]:
                res[(nailidx + 1) % n] = segments[nail].pop()
            else:
                break
        else:
            return ('YES', res)
    return ('NO', [])

