from heapq import heappush, heappop

def calculate_minimum_sadness(N, D, trainers):
    data = {}
    heap = []
    for (d, t, s) in trainers:
        if d in data:
            data[d].append([t, s])
        else:
            data[d] = [[t, s]]
    for d in range(1, D + 1):
        if d in data:
            for v in data[d]:
                heappush(heap, [-1 * v[1], v[0]])
        if len(heap):
            x = heappop(heap)
            x[1] -= 1
            if x[1]:
                heappush(heap, x)
    ans = 1
    while len(heap):
        x = heappop(heap)
        ans += -1 * x[0] * x[1]
    return ans

