def find_friend_circles(M):
    N = len(M)
    students = set(range(N))
    num_circles = 1
    while students:
        num_circles += 1
        stack = [students.pop()]
        while stack:
            student = stack.pop()
            for i in range(N):
                if M[student][i] == 1 and i in students:
                    stack.append(i)
                    students.discard(i)
    return num_circles

