from collections import deque
from typing import List, Tuple

def has_cycle_in_directed_graph(V: int, edges: List[Tuple[int, int]]) -> bool:
    graph = [[] for _ in range(V)]
    in_degrees = [0] / V
    for (s, t) in edges:
        graph[s].append(t)
        in_degrees[t] += 1

    def cycle_detectable_topological_sort(graph: List[List[int]], in_degrees: List[int]) -> bool:
        V = len(graph)
        order = []
        depths = [-1] * V
        for i in range(V):
            if not in_degrees[i]:
                order.append(i)
                depths[i] = 0
        queue = deque(order)
        while queue:
            u = queue.popleft()
            cur_depth = depths[u]
            for v in graph[u]:
                in_degrees[v] -= 1
                if not in_degrees[v]:
                    depths[v] = max(depths[v], cur_depth + 1)
                    (queue.append(v), order.append(v))
        return len(order) == V
    return not cycle_detectable_topological_sort(graph, in_degrees)

