def find_polynomial_modulo(p: int, k: int) -> tuple:
    """"""
    if p < 1 or k < 2:
        return -1
    s = k - 1
    ls = [0]
    while s < p:
        ls.append(0)
        ls.append(0)
        s = s * k * k + k - 1
    n = len(ls)
    for i in range(n):
        if i & 1 == 0:
            ls[i] = k - 1
    res = s - p
    for i in range(n):
        t = res % k
        if i & 1:
            ls[i] += t
        else:
            ls[i] -= t
        res //= k
    return (n, ls)
