def can_rearrange_gold_pieces(n, x, weights):
    if sum(weights) != x:
        return ('NO', None)
    l = []
    s = 0
    reject_list = []
    for weight in weights:
        s += weight
        if s == x:
            reject_list.append(weight)
            s -= weight
        else:
            l.append(weight)
    l.extend(reject_list)
    return ('YES', l)

