def minimize_bracket_operations(s: str) -> (int, list):
    j = len(s)
    n = len(s)
    ans = []
    for i in range(n):
        if i >= j:
            break
        if s[i] == '(':
            while True:
                j -= 1
                if i >= j:
                    break
                if s[j] == ')':
                    ans.append(i)
                    ans.append(j)
                    break
    if len(ans) != 0:
        return (0, [])
    k = 1
    operations = [sorted([ans[i] + 1 for i in range(len(ans))])]
    return (k, operations)

