def calculate_minimum_coins(test_cases):
    results = []
    for case in test_cases:
        n = case['']
        wheel = case['wheel']
        queries = case['queries']
        d = {}
        for i in range(n):
            if wheel[i] in d:
                d[wheel[i]].append(i)
            else:
                d[wheel[i]] = [i]
        for m in queries:
            indices = d[m]
            if len(indices) == 1:
                results.append(n)
            elif len(indices) == 2:
                results.append(min(indices[1] - indices[0], n - indices[1] + indices[0]))
            else:
                k = 100000
                for j in range(len(indices) - 1):
                    if indices[j + 1] - indices[j] < k:
                        k = indices[j + 1] - indices[j]
                results.append(min(k, n - indices[len(indices) - 1] + indices[0]))
    return results

