def find_original_string(n, k, s_prime):
    """"""
    beg = 0
    end = k - 1
    res = [''] * n
    for i in range(k):
        if not i & 1:
            res[i] = s_prime[beg]
            beg += 1
        else:
            res[i] = s_prime[end]
            end -= 1
    res[:k] = res[k - 1::-1]
    for i in range(k, n):
        res[i] = s_prime[i]
    return ''.join(res)
