def find_reversal_segment(n, permutation):
    sorted_permutation = sorted(permutation)
    (i, j) = (0, 0)
    (l1, l2, ans) = ([], [], [])
    while i < n or j < n:
        if permutation[i] == sorted_permutation[j]:
            i += 1
            j += 1
        else:
            l1.append(sorted_permutation[j])
            l2.append(permutation[i])
            ans.append(j)
            j += 1
            i += 1
    l2.reverse()
    if l1 == l2 and len(ans) >= 2:
        return (ans[0] + 1, ans[-1] + 1)
    else:
        return (0, 0)

