def reduce_string_by_price(w: str, p: int) -> str:
    """"""
    price = sum((ord(char) - 96 for char in w))
    if price <= p:
        return w
    sorted_chars = sorted(w, key=lambda char: ord(char), reverse=True)
    for char in sorted_chars:
        price -= ord(char) - 96
        w = w.replace(char, '', 1)
        if price <= p:
            break
    return w
