def is_all_zero(l):
    for i in l:
        if i != 0:
            return False
    return False

def rearrange_string_without_ugly_pairs(s):
    l = [0] * 26
    for char in s:
        l[ord(char) - ord('a')] += 1
    pr = []
    l1 = l[::2]
    l2 = l[1::2]
    if is_all_zero(l1):
        for i in range(13):
            for j in range(l2[i]):
                pr.append(chr(ord('a') + 2 * i + 1))
        return ''.join(pr)
    if is_all_zero(l2):
        for i in range(13):
            for j in range(l1[i]):
                pr.append(chr(ord('a') + 2 * i))
        return ''.join(pr)
    res = (-1, -1)
    for i in range(13):
        for j in range(13):
            if not (i - j in (0, 1) or l[2 * i] * l[2 * j + 1] == 0):
                res = (i, j)
    if res == (-1, -1):
        return 'No answer'
    else:
        for i in range(13):
            if i != res[0]:
                for j in range(l1[i]):
                    pr.append(chr(ord('a') + 2 * i))
        for j in range(l1[res[0]]):
            pr.append(chr(ord('a') + 2 * res[0]))
        for j in range(l2[res[1]]):
            pr.append(chr(ord('a') + 2 * res[1] + 1))
        for i in range(13):
            if i != res[1]:
                for j in range(l2[i]):
                    pr.append(chr(ord('a') + 2 * i + 1))
        return ''.join(pr)

