def min_operations_to_satisfy_condition(n, k, b):
    temp = 1
    operations = [0] * n
    last = 0
    for i in range(n - 1, k - 1, -1):
        now = 0
        if i != n - 1:
            last -= temp
            now = last
            if i + k >= n:
                pass
            else:
                temp -= operations[i + k]
        delta = b[i] - now
        if delta <= 0:
            continue
        else:
            operations[i] = (delta + k - 1) // k
            temp += operations[i]
            last = operations[i] * k + now
    m = 0
    need = [0] * k
    for i in range(k - 1, -1, -1):
        if i != n - 1:
            last -= temp
            need[i] = max(0, b[i] - last)
            if i + k >= n:
                pass
            else:
                temp -= operations[i + k]
        else:
            need[i] = max(0, b[i])
    for i in range(k):
        m = max(m, (need[i] + i) // (i + 1))
    return sum(operations) + m

