def find_min_max_with_digit_sum(l, d, x):

    def digit_sum(num):
        return sum(map(int, str(num)))
    valid_numbers = [num for num in range(l, d + 1) if digit_sum(num) != x]
    return [min(valid_numbers), max(valid_numbers)]

