def update_arithmetic_progression(N, a, d, M, statements, K):
    mem = {}

    def get_mem(num):
        if num in mem:
            return mem[num]
        else:
            return num
    for (x, y, z) in statements:
        if x == 0:
            (ny, nz) = (get_mem(y), get_mem(z))
            mem[y] = nz
            mem[z] = ny
        if x == 1:
            nz = get_mem(z)
            mem[y] = nz
    k_value = get_mem(K)
    return a - d * (k_value - 1)

