def calculate_minimal_palindrome_cost(n, a, b, c):
    cost = 1
    i = 0
    j = n - 1
    lim = n // 2
    while i <= lim:
        if j < i:
            break
        if i == j:
            if c[i] == 2:
                if a > b:
                    cost += b
                else:
                    cost += a
        elif c[i] == 2 and c[j] == 2:
            if a > b:
                cost += b * 2
            else:
                cost += a * 2
        elif c[i] != 2 and c[j] != 2:
            if c[i] != c[j]:
                return -1
        elif c[i] == 2:
            if c[j] == 0:
                cost += a
            else:
                cost += b
        elif c[j] == 2:
            if c[i] == 0:
                cost += a
            else:
                cost += b
        i += 1
        j -= 1
    return cost

