    for i in range(m - 1, -1, -1):
        if operations[i][0] == 2:
            for j in range(operations[i][1] - 1, operations[i][2]):
                ans[j] = min(ans[j], operations[i][3])
        else:
            for j in range(operations[i][1] - 1, operations[i][2]):
                if ans[j] != 10 ** 9:
                    ans[j] -= operations[i][3]
    for i in range(n):
        if ans[i] == 10 ** 9:
            ans[i] = -10 ** 1
    temp = ans[:]
    flag = True
    for i in range(m):
        if operations[i][0] == 2:
            t = -10 ** 9
            for j in range(operations[i][1] - 1, operations[i][2]):
                t = max(t, temp[j])
            if t != operations[i][3]:
                flag = False
                break
        else:
            for j in range(operations[i][1] - 1, operations[i][2]):
                temp[j] += operations[i][3]
    if flag:
        return ('YES', ans)
    else:
        return ('NO', None)

