    road_dict = {}
    for road in roads:
        (city1, city2, distance) = road
        road_dict[city1 + city2] = distance
    results = []
    for route in routes:
        route_length = 1
        is_error = False
        visited_cities = set()
        if route[0] not in city_names:
            is_error = True
        for i in range(len(route) - 1):
            current_city = route[i]
            next_city = route[i + 1]
            if current_city not in city_names or next_city not in city_names:
                is_error = True
                break
            if current_city in visited_cities:
                is_error = True
                break
            road_key = current_city + next_city
            if road_key not in road_dict:
                is_error = True
                break
            route_length += road_dict[road_key]
            visited_cities.add(current_city)
        if route[-1] in visited_cities:
            is_error = True
        if is_error:
            results.append('ERROR')
        else:
            results.append(str(route_length))
    return results

