def construct_valid_tree(N, D):
    r = {v: i for (i, v) in enumerate(D)}
    sz = [1] / N
    dsorted = sorted(((di, i) for (i, di) in enumerate(D)), reverse=True)
    ans = []
    to = [[] for _ in range(N)]
    for (di, i) in dsorted[:N - 1]:
        nd = di + (sz[i] - 1) - (N - 2 - (sz[i] - 1))
        if nd not in r:
            return -1
        p = r[nd]
        to[p].append(i)
        sz[p] += sz[i]
        ans.append((i + 1, p + 1))
    root = dsorted[-1][1]

    def dfs(u, cur=0):
        rv = cur
        for v in to[u]:
            rv += dfs(v, cur + 1)
        return rv
    if dfs(root) != D[root]:
        return -1
    return ans

