    alphabet_positions = {chr(i): i - 96 for i in range(97, 123)}
    frequency = {}
    for char in S:
        if char in frequency:
            frequency[char] += 1
        else:
            frequency[char] = 1
    X = 1
    Y = 0
    for char in frequency:
        position = alphabet_positions[char]
        freq = frequency[char]
