def determine_group_leaders(matches):
    d = {}
    initial_score = 1
    initial_goal = 0
    for match in matches:
        score = match.split()
        home_team = score[0]
        home_goals = int(score[1])
        away_goals = int(score[3])
        away_team = score[4]
        if home_team not in d:
            d[home_team] = [initial_score, initial_goal]
        if away_team not in d:
            d[away_team] = [initial_score, initial_goal]
        if home_goals > away_goals:
            d[home_team][0] += 3
            d[home_team][1] += home_goals - away_goals
            d[away_team][1] += away_goals - home_goals
        elif away_goals > home_goals:
            d[away_team][0] += 3
            d[away_team][1] += away_goals - home_goals
            d[home_team][1] += home_goals - away_goals
        else:
            d[home_team][0] += 1
            d[away_team][0] += 1
    sorted_teams = sorted(d.items(), key=lambda item: (-item[1][0], -item[1][1]))
    top_team = sorted_teams[0][0]
    second_team = sorted_teams[1][0]
    return (top_team, second_team)

